const STATE = require('../../../../src/node_modules/STATE')
const statedb = STATE(__filename)
const { sdb, get } = statedb(fallback_module)

/******************************************************************************
  BTN
******************************************************************************/
const icon = require('icon')

module.exports = {btn, btn_small}
async function btn(opts) {
  // ----------------------------------------
  // ID + JSON STATE
  // ----------------------------------------
  let msg_cache = {}
  const { id, sdb, io, net } = await get(opts.sid) // hub is "parent's" io "id" to send/receive messages
  console.log(net)
  const on = {
    theme: inject,
    lang: fill
  }
  // ----------------------------------------
  // TEMPLATE
  // ----------------------------------------
  const el = document.createElement('div')
  const shopts = { mode: 'closed' }
  const shadow = el.attachShadow(shopts)
  shadow.innerHTML = `
    <button>
      <span></span>
    </button>
    <style>
      button{
        padding: 10px 40px;
      }
    </style>`
  const style = shadow.querySelector('style')
  const button = shadow.querySelector('button')
  const title = shadow.querySelector('button > span')
  const subs = await sdb.watch(onbatch)
  // ----------------------------------------
  // ELEMENTS
  // ----------------------------------------
  {
    button.append(await icon(subs[0]))
  }
  // ----------------------------------------
  // EVENT LISTENERS
  // ----------------------------------------
  button.onclick = () => {
    net.event.click.forEach(msg => {
      msg_cache.type = msg.type
      msg_cache.args = msg.args
      io.at(msg.id)
    })
  }
  io.on(port => {
    const { by, to } = port
    console.log('btn', msg_cache)
    port.onmessage = event => {
      const txt = event.data
      const key = `[${by} -> ${to}]`
      console.log(key, txt)
    }
    port.postMessage(msg_cache)
  })
  return el

  function onbatch(batch){
    for (const {type, data} of batch) {
      on[type] && on[type](data)
    }
  }
  async function inject (data){
    style.innerHTML = data.join('\n')
  }
  async function fill([data]) {
    title.replaceChildren(data.title)
  }
}
async function btn_small(opts) {
  // ----------------------------------------
  // ID + JSON STATE
  // ----------------------------------------
  const { id, sdb } = await get(opts.sid) // hub is "parent's" io "id" to send/receive messages
  const on = {
    css: inject,
    json: fill
  }
  // ----------------------------------------
  // TEMPLATE
  // ----------------------------------------
  const el = document.createElement('div')
  const shopts = { mode: 'closed' }
  const shadow = el.attachShadow(shopts)
  shadow.innerHTML = `
    <button></button>
    <style></style>`
  const style = shadow.querySelector('style')
  const button = shadow.querySelector('button')
  const subs = await sdb.watch(onbatch)
  // ----------------------------------------
  // ELEMENTS
  // ----------------------------------------
  {
    button.append(await icon(subs[0]))
  }
  return el

  function onbatch(batch){
    for (const {type, data} of batch) {
      on[type] && on[type](data)
    }
  }
  async function inject (data){
    style.innerHTML = data.join('\n')
  }
  async function fill([data]) {
    button.innerHTML = data.title
  }
}


function fallback_module () {
  return {
    api: fallback_instance,
    _: { icon: {$: ''} }
  }
  function fallback_instance () {
    return {
      _: { icon: {0: ''} },
      drive: {
        'lang/': {
          'en-us.json': {
            raw: {
              title: 'Click me'
            }
          }
        },
      },
      net: {
        api: ['inject', 'fill'],
        event: {
          'click': [],
        }
      }
    }
  }
}
